def fizzbuzz(n):
    if n % 15 == 0:
        return 'FizzBuzz'
    elif n % 3 == 0:
        return 'Fizz'
    elif n % 5 == 0:
        return 'Buzz'
    else:
        return str(n)

def is_palindrome(s):
    s = s.lower().replace(' ', '')
    return s == s[::-1]

def two_sum(nums, target):
    seen = {}
    for i, num in enumerate(nums):
        complement = target - num
        if complement in seen:
            return [seen[complement], i]
        seen[num] = i
    return []

def myAtoi(s):
    if not s:
        return 0

    i = 0
    n = len(s)

    while i < n and s[i] == ' ':
        i += 1

    if i == n:
        return 0

    sign = 1
    if s[i] == '-':
        sign = -1
        i += 1
    elif s[i] == '+':
        i += 1

    result = 0
    while i < n and s[i].isdigit():
        digit = int(s[i])
        if result > (2**31 - 1 - digit) // 10:
            return 2**31 - 1 if sign == 1 else -2**31
        result = result * 10 + digit
        i += 1

    return sign * result
